                {
                    driver = new AppiumDriver();
                }


                [Test]
                public void TestGoogle()
                {
                    driver.Navigate().GoToUrl("http://www.google.co.uk");
                    /*
                    *   Rest of the test
                    */
                }


                [TearDown]
                public void TearDown()
                {
                    driver.Quit();
                    driver.Dispose();
                } 
            }

[Test]
